import React, { Component } from "react";
import Title from "./Title";
import { FaCocktail, FaHiking, FaShuttleVan, FaBeer } from "react-icons/fa";

export default class Services extends Component {
  state = {
    services: [
      {
        icon: <FaCocktail />,
        title: "free cocktails",
        info: "Lorem ipsum dolor, sit amet consectetur adipisicing elit. Autem culpa consectetur assumenda excepturi magnam non numquam cum, quasi, dolore sed, recusandae dolores quam modi aperiam quisquam itaque ipsa quos tenetur?",
      },
      {
        icon: <FaHiking />,
        title: "free hiking",
        info: "Lorem ipsum dolor, sit amet consectetur adipisicing elit. Autem culpa consectetur assumenda excepturi magnam non numquam cum, quasi, dolore sed, recusandae dolores quam modi aperiam quisquam itaque ipsa quos tenetur?",
      },
      {
        icon: <FaShuttleVan />,
        title: "free shuttle",
        info: "Lorem ipsum dolor, sit amet consectetur adipisicing elit. Autem culpa consectetur assumenda excepturi magnam non numquam cum, quasi, dolore sed, recusandae dolores quam modi aperiam quisquam itaque ipsa quos tenetur?",
      },
      {
        icon: <FaBeer />,
        title: "Strongest Beer",
        info: "Lorem ipsum dolor, sit amet consectetur adipisicing elit. Autem culpa consectetur assumenda excepturi magnam non numquam cum, quasi, dolore sed, recusandae dolores quam modi aperiam quisquam itaque ipsa quos tenetur?",
      },
    ],
  };
  render() {
    return (
      <section className="services">
        <Title title="services" />
        <div className="services-center">
          {this.state.services.map((item, index) => {
            return (
              <article key={index} className="services">
                <span>{item.icon}</span>
                <h6>{item.title}</h6>
                <p>{item.info}</p>
              </article>
            );
          })}
        </div>
      </section>
    );
  }
}
